css:
.ksa-invoice-table {
  border: 1px solid ;
  border-collapse: collapse;
  width: 100%;
  margin: 20px 0;
  font-size: 16px;
  align-items : center;


}

.ksa-invoice-table.two-columns td:nth-child(2) {
  text-align: center;
  vertical-align: middle;
}

.ksa-invoice-table th {
  border: 1px solid #888a8e;
  max-width: 50%;
  padding: 8px;
  text-align: center;
  vertical-align: middle;


}

.ksa-invoice-table td {
  padding: 5px;
  border: 1px solid #888a8e;
  max-width: 50%;
  text-align: center;
  vertical-align: middle;

}




.print-format th {
    background-color: #a1b6ce !important;
    border-bottom: 0px !important;
    text-align: center;
  vertical-align: middle;
      color: black;
}

.print-format td {
      text-align: center;
  vertical-align: middle;
}
